mapper.select:
select count(*) totalCnt,min(price) bottomPrice,avg(price) averagePrice,max(price) topPrice from goods where status!=4 and active_status='Y'

	and category_id=#{category}


	and title like #{searchWord}



	and price <= 100000


	and price >= 100000 and price <= 300000


	and price >= 300000 and price <= 500000


	and price >= 500000



	and dong_id=#{dongId}

select list2.*,(select f.url from goods_file f where f.goods_id=list2.goods_id and rownum between 0 and 1) url from (select list.*,rownum rn from( (select g.view_count,g.title,g.price,g.goods_Id,u.nickname,g.status,g.safe_Trading_Yn,d.dong_Name,g.refreshed_at, g.created_at,
(select count(*) from wishList w where g.goods_id=w.goods_id) likeCount from goods g join users u using(user_id) join dong d on g.dong_id=d.dong_id where status!=4 and active_status='Y'
and category_id=#{category}
and title like #{searchWord}
and price <= 100000
and price >= 100000 and price <= 300000
and price >= 300000 and price <= 500000
and price >= 500000
and g.dong_id=#{dongId}
order by likeCount desc, g.refreshed_at desc
order by price asc, g.refreshed_at desc
order by price desc, g.refreshed_at desc
order by g.refreshed_at desc
) list) )list2 where list2.rn between #{startRownum} and #{endRownum}

select u.dong_id, d.dong_name, g.gu_id, g.gu_name
from users u join dong d on u.dong_Id=d.dong_Id join gu g on d.gu_Id=g.gu_Id
where user_id=#{userId}

mapper.update:

update goods 
set title=#{title},category_id=#{category_id},
price=#{price},description=#{description}
,safe_trading_yn=#{safeTradingYN}
,dong_id=#{dong_id},lat=#{lat},lng=#{lng}
where goods_id=#{goodsId}
